mapper.select:
select mem_id
  from member
 where git_nick = #{gitNick}

mapper.update:
update alarmlist
   set alarm_yn = 0
 where alarm_no = #{alarmNo}

update member
   set team=#{proNo}
     , team_status='Y'
 where mem_id=#{proWriter}

update alarmlist
   set status = 'N'
 where alarm_no = #{alarmNo}

update alarmlist
   set status = 'N'
 where mem_id = #{memId}
   and rmem_id = #{rmemId}
   and alarm_type = #{alarmType}

mapper.select:
select count(*)
  from alarmlist
 where alarm_content_no = #{alarmContentNo}
