mapper.insert:
insert
  into calendar
  (
  	cal_no
  , cal_title
  , start_date
  , end_date
  , groupid
  , background_col
  )
  values
  (
  	seq_cdno.nextval
  , #{calTitle}
  , #{startDate}
  , #{endDate}
  , #{groupId}
  , #{backgroundCol}
  )

mapper.select:
select count(*)
  from calendar
 where cal_title=#{calTitle}
   and start_date=#{startDate}
   and end_date=#{endDate}
   and status='Y'

mapper.update:
update calendar
   set start_date=#{startDate}
   	 , end_date=#{endDate}
 where cal_no=#{calNo}

update calendar
   set status='N'
 where cal_no=#{eNo}
