mapper.insert:
insert
      into project
      (
        pro_no
      , pro_writer
      , pro_stack
      , pro_title
      , pro_content
      , pro_member
      , pro_period
      , pro_deadline
      , pro_start_date
      , pro_position
      , pro_status
      )
      values
      (
        seq_pno.nextval
      , #{proWriter}
      , #{proStack}
      , #{proTitle}
      , #{proContent}
      , #{proMember}
      , #{proPeriod}
      , #{deadLine}
      , #{startDate}
      , #{positoin}
      , 'Y'
      )

mapper.update:
update member
   set team_status = 'S'
	  , team = #{pno}
  where mem_id = #{userId}

update project
   set pro_status = 'N'
 where pro_writer = #{userId}
   and pro_no = #{pno}

update project
   set pro_status = 'Y'
 where pro_writer = #{userId}
   and pro_no = #{pno}

update
       project
   set pro_stack = #{proStack}
     , pro_title = #{proTitle}
     , pro_content = #{proContent}
     , pro_member = #{proMember}
     , pro_period = #{proPeriod}
     , pro_deadline = #{deadLine}
     , pro_start_date = #{startDate}
     , pro_position = #{positoin}
     , create_date = SYSDATE
 where pro_no = #{proNo}

update reply
   set status = 'N'
 where reply_no = #{pno}
